mapper.select:
select h.id as id, huser_name, DATE_FORMAT(hwrite_date, '%y.%m.%d') as hwrite_date, htype, date_format(hstart_date,'%y.%m.%d') as hstart_date, date_format(hend_date,'%y.%m.%d') as hend_date, hcontent, half_day,approval, count_date, h.empno as empno, e.total_holiday as total_holiday, date_format(approval_date,'%y.%m.%d') 
FROM holiday h, employee_info e
	
	h.empno = e.empno
	and h.empno=#{empno}
	
		and 1=1
	
	
		and DATE_FORMAT(hwrite_date, '%Y') = #{hwriteDate}
	

order by DATE_FORMAT(hwrite_date, '%y.%m.%d %H:%i:%m') desc;

select distinct(DATE_FORMAT(hwrite_date, '%Y')) 
from holiday
where empno=#{empno}
