mapper.update:
update bbs 

	, category = #{category}
	, title = #{title}
	, content = #{content}
	, file_counts = #{fileCounts}

where id = #{id} and owner_id = #{ownerId}

mapper.select:
select count(*)
from bbs as b inner join bbs_uploadfile as f
on b.id = #{id} and f.bbs_id = #{id};

select id, bbs_id, localname, servername
from bbs_uploadfile
where bbs_id = #{bbsId}

mapper.delete:
delete from bbs_uploadfile

	
		
			and id = #{id}
		
		
			and bbs_id = #{bbsId}
