mapper.select:
select h.id as id, huser_name, hwrite_date, htype, hstart_date, hend_date, hcontent, half_day,approval, h.empno as empno, e.total_holiday as total_holiday 
FROM holiday h, employee_info e
	
	h.empno = e.empno
	and h.empno=#{empno}
	
		and 1=1
	
	
		and DATE_FORMAT(hwrite_date, '%Y') = #{hwriteDate}
	

order by hwrite_date desc;

select distinct(DATE_FORMAT(hwrite_date, '%Y')) 
from holiday
where empno=#{empno}
